import {IResType, mewRequest} from "../request";

export interface RabbitClient {
    node:string
    serviceName:string
}

export function Rabbit(service: any, isDebugMode: boolean = false) {
    return {
        async getClients():Promise<IResType<RabbitClient>> {
            return await mewRequest('/rabbit/clients', {}, 'GET', service, isDebugMode)
        },
        async getNodes():Promise<IResType<RabbitClient>> {
            return await mewRequest('/rabbit/nodes', {}, 'GET', service, isDebugMode)
        }
    }
}
