import {Mc, Tc} from '../../src/main'
import {AuthData} from "../../src/ms/mc";
import {FaceInsertProperty} from "../../src/ms/uc";
import axios from "axios";

// const baseURL = 'http://www.mew-iot.cn:51000'
const baseURL = 'https://api.mew-iot.cn'

let service = axios.create({
    baseURL ,
    // adapter: axiosAdapterUniapp as AxiosAdapter,
    timeout: 10000
})
let authData:AuthData
let uc

describe('uc', () => {
    beforeAll(async () => {
        const mc = Mc(service)
        const res = await mc.getAuth({username: 'admin', password: '111111'})
        authData = res.data
        const access_token = authData.access_token
        service = axios.create({
            baseURL,
            // adapter: axiosAdapterUniapp as AxiosAdapter,
            timeout: 10000,
            headers:{
                'Authorization': 'Bearer ' + access_token
            }
        })
        uc = Tc(service)
    })

    test('face表删除featureData=1647952611192',async () => {
        const res = await uc.face.delete({featureData: '1647953368448'})
        expect(res.code).toBe(0)
        expect(res.msg).toBe('success')
        expect(res.data.length).toBeGreaterThanOrEqual(0)
    })

    test(`确保face表包含一条有效数据`, async() => {
        const res= await uc.face.select({featureData: '1647952611192'})
        if (res.data.length == 0){
            const json:FaceInsertProperty = {featureData: "1647952611192"}
            const res = await uc.face.insert(json)
            expect(res.code).toBe(0)
            expect(res.msg).toBe('success')
            expect(res.data.length).toBe(1)
            expect(res.data[0].featureData).toBe(json.featureData)
        }
    },2000)
})
