import axios from "axios";
import {Uc} from "../../src/ms/uc";
import {AuthData, Mc} from "../../src/ms/mc";
import {Tc} from "../../src/ms/tc";

// const baseURL = 'http://www.mew-iot.cn:51000'
const baseURL = 'https://api.mew-iot.cn'

let service = axios.create({
    baseURL ,
    // adapter: axiosAdapterUniapp as AxiosAdapter,
    timeout: 10000
})
let authData:AuthData
let uc

describe('ding_talk_user',()=>{
    beforeAll(async () => {
        const mc = Mc(service)
        const res = await mc.getAuth({username: 'admin', password: '111111'})
        authData = res.data
        const access_token = authData.access_token
        service = axios.create({
            baseURL,
            // adapter: axiosAdapterUniapp as AxiosAdapter,
            timeout: 10000,
            headers:{
                'Authorization': 'Bearer ' + access_token
            }
        })
        uc = Tc(service)
    })

    test(`ding_talk_user select`, async() => {
        const json ={appId: '5000000001708659', unionid: '4Z8djj1BAka6ISWA6GQrdQiEiE'}
        const res= await uc.ding_talk_user.select(json)
        console.log(res)
    },2000)
})
